SELECT aa.assessid
, aa.common
, aa.area
, aa.points_available
, aa.bmsy_unit
, aa.ssbmsy_unit
, aa.blim_unit
, aa.ssblim_unit
, aa.ssbpa_unit
, aa.ssbtarget_unit
, aa.umsy_unit
, aa.fmsy_unit
, aa.flim_unit 
FROM (
SELECT biopar.assessid
     , stock.commonname AS common
     , area.areaname AS area
     , count
     (*) AS points_available
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'Bmsy-MT'::text
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS bmsy_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = ANY 
                             (
                                   (ARRAY['SSBmsy-MT'::character varying
                                         ,'SSBmsy-E03egss'::character varying
                                         ,'SSBmsy-E06larvae'::character varying]
                                   )::text[]
                             )
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS ssbmsy_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'Blim-MT'::text
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS blim_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'SSBlim-MT'::text
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS ssblim_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'SSBpa-MT'::text
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS ssbpa_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'SSBtarget-MT'::text
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS ssbtarget_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'Umsy-ratio'::text
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS umsy_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = ANY 
                             (
                                   (ARRAY['Fmsy-1/yr'::character varying
                                         ,'Fmsy-1/T'::character varying]
                                   )::text[]
                             )
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS fmsy_unit
     , min
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = ANY 
                             (
                                   (ARRAY['Flim-1/yr'::character varying
                                         ,'Flim-1/T'::character varying]
                                   )::text[]
                             )
                       )
                 ) THEN biometrics.biounitsshort ELSE NULL::character varying END
           )::text
     ) AS flim_unit 
  FROM srdb.bioparams biopar
     , srdb.biometrics
     , srdb.assessment assess
     , srdb.stock stock
     , srdb.area area 
 WHERE (
           (
                 (
                       (
                             (biopar.bioid)::text = 
                             (biometrics.biounique)::text
                       )
                     AND (
                             (stock.stockid)::text = 
                             (assess.stockid)::text
                       )
                 )
               AND (
                       (stock.areaid)::text = 
                       (area.areaid)::text
                 )
           )
         AND (
                 (biopar.assessid)::text = 
                 (assess.assessid)::text
           )
     )
GROUP BY biopar.assessid
     , stock.commonname
     , area.areaname 
ORDER BY biopar.assessid
) aa;

SELECT aa.assessid
, aa.common
, aa.area
, aa.points_available
, aa.bmsy
, aa.ssbmsy
, aa.blim
, aa.ssblim
, aa.ssbpa
, aa.ssbtarget
, aa.umsy
, aa.fmsy
, aa.flim 
FROM (
SELECT biopar.assessid
     , stock.commonname AS common
     , area.areaname AS area
     , count
     (*) AS points_available
     , max
     (
           (CASE WHEN 
                 (
                       (upper
                             (
                                   (biometrics.subcategory)::text
                             ) = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'Bmsy-MT'::text
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS bmsy
     , max
     (
           (CASE WHEN 
                 (
                       (upper
                             (
                                   (biometrics.subcategory)::text
                             ) = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = ANY 
                             (
                                   (ARRAY['SSBmsy-MT'::character varying
                                         ,'SSBmsy-E03egss'::character varying
                                         ,'SSBmsy-E06larvae'::character varying]
                                   )::text[]
                             )
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS ssbmsy
     , max
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'Blim-MT'::text
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS blim
     , max
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'SSBlim-MT'::text
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS ssblim
     , max
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'SSBpa-MT'::text
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS ssbpa
     , max
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'SSBtarget-MT'::text
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS ssbtarget
     , max
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = 'Umsy-ratio'::text
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS umsy
     , max
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = ANY 
                             (
                                   (ARRAY['Fmsy-1/yr'::character varying
                                         ,'Fmsy-1/T'::character varying]
                                   )::text[]
                             )
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS fmsy
     , max
     (
           (CASE WHEN 
                 (
                       (
                             (biometrics.subcategory)::text = 'REFERENCE POINTS ETC.'::text
                       )
                     AND (
                             (biometrics.biounique)::text = ANY 
                             (
                                   (ARRAY['Flim-1/yr'::character varying
                                         ,'Flim-1/T'::character varying]
                                   )::text[]
                             )
                       )
                 ) THEN biopar.biovalue ELSE NULL::character varying END
           )::text
     ) AS flim 
  FROM srdb.bioparams biopar
     , srdb.biometrics
     , srdb.assessment assess
     , srdb.stock stock
     , srdb.area area 
 WHERE (
           (
                 (
                       (
                             (biopar.bioid)::text = 
                             (biometrics.biounique)::text
                       )
                     AND (
                             (stock.stockid)::text = 
                             (assess.stockid)::text
                       )
                 )
               AND (
                       (stock.areaid)::text = 
                       (area.areaid)::text
                 )
           )
         AND (
                 (biopar.assessid)::text = 
                 (assess.assessid)::text
           )
     )
GROUP BY biopar.assessid
     , stock.commonname
     , area.areaname 
ORDER BY biopar.assessid
) aa;

