'CATCH or LANDINGS'::text
)                       
AND                      (
(                 tm.tsunique)::text = 'TL-1-MT'::text
)                       
)                              THEN tm.tsunitsshort ELSE NULL::character varying END
)                                   ::text
)                                          ELSE CASE WHEN srdb_metric_boolean
(                                         
'CATCH or LANDINGS'                 ::text
,                                   'TC-E03'::text
,                                    (ts.assessid)::text
)                                          THEN min
(                                         
(                                   CASE WHEN 
(                             
(                       
(                 tm.tscategory)::text = 
'CATCH or LANDINGS'::text
)                       
AND                      (
(                 tm.tsunique)::text = 'TC-E03'::text
)                       
)                              THEN tm.tsunitsshort ELSE NULL::character varying END
)                                   ::text
)                                          ELSE CASE WHEN srdb_metric_boolean
(                                         
'CATCH or LANDINGS'                 ::text
,                                   'TL-E03'::text
,                                    (ts.assessid)::text
)                                          THEN min
(                                         
(                                   CASE WHEN 
(                             
(                       
(                 tm.tscategory)::text = 
'CATCH or LANDINGS'::text
)                       
AND                      (
(                 tm.tsunique)::text = 'TL-E03'::text
)                       
)                              THEN tm.tsunitsshort ELSE NULL::character varying END
)                                   ::text
)                                          ELSE CASE WHEN srdb_metric_boolean
(                                         
'CATCH or LANDINGS'                 ::text
,                                   'TC-1-E03'::text
,                                    (ts.assessid)::text
)                                          THEN min
(                                         
(                                   CASE WHEN 
(                             
(                       
(                 tm.tscategory)::text = 
'CATCH or LANDINGS'::text
)                       
AND                      (
(                 tm.tsunique)::text = 'TC-1-E03'::text
)                       
)                              THEN tm.tsunitsshort ELSE NULL::character varying END
)                                   ::text
)                                          ELSE min
(                                         
(                                   CASE WHEN 
(                             
(                       
(                 tm.tscategory)::text = 
'CATCH or LANDINGS'::text
)                       
AND                      (
(                 tm.tsunique)::text = 'TL-1-E03'::text
)                       
)                              THEN tm.tsunitsshort ELSE NULL::character varying END
)                                   ::text
)                                          END END END END END END END AS catch_landings_unit 
FROM                                       srdb.timeseries ts
,                                          srdb.tsmetrics tm 
WHERE                                      (
(                                   ts.tsid)::text = 
(                                   tm.tsunique)::text
)                                         
GROUP BY                                   ts.assessid 
ORDER BY                                   ts.assessid;

SELECT aa.stockid
, bb.assessid
, bb.pdffile 
FROM srdb.tstruncation_summary aa
, srdb.assessment bb 
WHERE (
     (
           (
                 (
                       (aa.stockid)::text = 
                       (bb.stockid)::text
                 )
               AND (
                       (bb.recorder)::text <> 'MYERS'::text
                 )
           )
         AND (
                 (aa.tsid)::text ~~ 'SSB%'::text
           )
     )
   AND (aa."First year similar" = 'no'::text)
)
ORDER BY aa.stockid;

para.programlisting:
DECLARE
	result boolean;
	sqlquery text;
BEGIN
sqlquery:=  
	'select (count(distinct tsid)!=0) 
	from srdb.timeseries as ts1, srdb.tsmetrics
	where 
	tscategory =' || quote_literal(category)  || ' AND
	ts1.assessid='    || quote_literal(assessment)|| ' AND
	tsunique='    || quote_literal(metric)|| ' AND
	ts1.tsid=tsunique';
EXECUTE sqlquery
INTO result;
return result;
END;
