select aa.assessorid, s.stocklong, a.stockid, a.recorder, rd.risentry from srdb.referencedoc rd, srdb.assessment a, srdb.stock s, srdb.assessor aa where rd.assessid in (select assessid from srdb.assessment where assessorid in (select assessorid from srdb.assessor where mgmt = 'NMFS')) and rd.risfield = 'ID' and a.assessid=rd.assessid and a.stockid=s.stockid and aa.assessorid=a.assessorid order by aa.assessorid, a.stockid;
